import cv2import osimport sys# Load# sys.argv[1] is path to videocap = cv2.VideoCapture(sys.argv[1])# Create dirif not os.path.exists('frames'):    os.makedirs('frames')# first frame namecurrentFrame = 1# Capture frame-by-framewhile (True):    # ret = True is you read frame    ret, frame = cap.read()    if not ret:        break    # Saves frame    name = './frames/frame' + str(currentFrame) + '.jpg'    print('Creating ' + name)    cv2.imwrite(name, frame)    currentFrame += 1# close video capturecap.release()